mapper.update:
update BOARD set
BOARD_TITLE = #{bTitle}, BOARD_CONTENT = #{bContent} where BOARD_SEQ =
#{bSeq}

mapper.select:

select *
from (select ROWNUM rn, B.BOARD_SEQ, B.BOARD_TITLE, B.BOARD_REG_DAY, B.BOARD_LIKE, B.BOARD_COUNT, M.MEMBER_NICKNAME 
from MEMBER M join BOARD B on M.MEMBER_SEQ = B.MEMBER_SEQ where rownum <+ #{pageNum} * #{amount} ) where rn > (#{pageNum}-1)*#{amount}
